import React from 'react';
import { Routes, Route } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';
import { CurrentCardInfo } from '../contexts/CurrentCardInfo.js';
import Header from "../Header/Header.js";
import Main from "../Header/Header.js";
import Movies from "../Header/Header.js";
import SavedMovies from "../SavedMovies/SavedMovies.js";
import Profile from "../Profile/Profile.js";
import Register from "../Register/Register.js";
import Login from "../Login/Login.js";
import Footer from "../Footer/Footer.js";
import Error from "../Error/Error.js";
import './App.css';

function App() {
  const [currentUser, setCurrentUser] = useState(false);
  const [movies, setMovies] = useState([]);
  const [loggedIn, setLoggedIn] = useState(false)
  const [userEmail, setUserEmail] = useState("");



  return (
    <CurrentUserContext.Provider value={currentUser}>
      <CurrentCardInfo.Provider value={movies}>
        <div>
        <Header loggedIn={loggedIn} userEmail={userEmail} />
        <Routes>

          <Route path="/" element={<Main />} />;
          <Route path="/movies" element={<Movies />} />;
          <Route path="/saved-movies" element={<SavedMovies />} />;
          <Route path="/profile" element={<Profile />} />;

          <Route path="/sign-up" element={<Register />} />
          <Route path="/sign-in" element={<Login />} />
          <Route path="*" element={(<Error />)} />
        </Routes>

        <Footer />
        </div>
      </CurrentCardInfo.Provider>
    </CurrentUserContext.Provider>
  );
}

export default App;
