import React from "react";
import { useState, useLocation } from "react";
import './Profile.css';
import Header from "../Header/Header";

export default function Profile(props) {

    const location = useState();

    const [name, setName] = useState({ name: props.user.name });
    const [email, setEmail] = useState({ email: props.email.email });
    
    const [edit, setEdit] = useState(false) // состояние disabled

    const handleEmail = (e) => {
        setEmail(e.target.value)
    }
    const handleName = (e) => {
        setName(e.target.value)
    }

    function handleSaveEdit(e) {
        e.preventDefault()
        setEdit(false)
        props.editProfile(name, email)
    }

    return (
        <>
            <Header notLog={true} />
            <div className="profile">
                <h1 className="profile__name">Привет, {props.user.name}!</h1>
                <form className="profile__form">
                    <label className="profile__input">
                        Имя
                        <input
                            className="profile__input-name"
                            type="text"
                            disabled={!edit}
                            onChange={handleName}
                            value={name.name || ''}>
                        </input>
                    </label>

                    <label className="profile__input">
                        E-mail
                        <input
                            className="profile__input-name"
                            type="email"
                            disabled={!edit}
                            onChange={handleEmail}
                            value={email.email || ''}>
                        </input>
                    </label>
                </form>
            </div>
        </>
    )
}
