import React from "react";
import { useState, useLocation } from "react";
import './Profile.css';
import Header from "../Header/Header";

export default function Profile(props){

    const location = useState();
    const [editProfile, setEditProfile] = useState({
        name: props.user.name,
        email: props.user.email
    })

    const [email, setEmail] = useState({email: props.user.email});
    const [name, setName] = useState({name: props.user.name});
    const [edit, setEdit] = useState(false) // состояние disabled

    const handleEmail = (e) => {
        setEmail(e.target.value)
      }
      const handlePassword = (e) => {
        setPassword(e.target.value)
      }
    function handleEditInput(e) {
        const{name , value} = e.target;
        setEditProfile({...editProfile, [name]: value})
    }

    function handleSaveEdit(e) {
        e.preventDefault()
        setEdit(false)
        props.editProfile(editProfile)
    }

    return(
        <>
        <Header notLog={true}/>
        <div className="profile">
            <h1 className="profile__name">Привет, {props.user.name}!</h1>
            <form className="profile__form">
                <label className="profile__input">
                    Имя
                   <input 
                   className="profile__input-name"
                   type="text"
                   disabled = {!edit}
                   value={editProfile.name}>
                   </input>
                </label>

                <label className="profile__input">
                    E-mail
                   <input 
                   className="profile__input-name"
                   type="email"
                   disabled = {!edit}
                   value={editProfile.email}>
                   </input>
                </label>
            </form>
        </div>
        </>
    )
}
