import React from 'react';
import './Promo.css';

export default function Promo() {
    const about = document.querySelector('.aboutproject');

    // function scroll(e) {
    //     e.preventDefault();
    // 	// var plansOffset = ("#js-plans").offset().top;

    // 	("html, body").animate({
    // 		scrollTop: plansOffset
    // 	}, 1000);
    //     };

        const anchors = [].slice.call(document.querySelectorAll('a[href*="#"]'))
        for (let anchor of anchors) {
            anchor.addEventListener('click', function (e) {
                e.preventDefault()
                const blockID = anchor.getAttribute('href').split('#')[1]
                document.getElementById(blockID).scrollIntoView({
                    behavior: 'smooth',
                    block: 'start',
                })
            })
        }

    return (
        <promo className='promo'>
            <div className='promo__text'>
                <p className='promo__big-text'>Учебный проект студента факультета Веб-разработки.</p>
                <p className='promo__small-text'>Листайте ниже, чтобы узнать больше про этот проект и его создателя.</p>
                <button className='promo__info' type='button' >Узнать больше</button>
            </div>
            <div className='promo__image' />

        </promo>
    )
}
