import React from 'react';
import { useLocation } from "react-router-dom";
import "./Burger.css";

export default function Burger(props) {
    const location = useLocation();

    const isLocationMain = location.pathname === "/";
    const isLocationMovies = location.pathname === "/movies";
    const isLocationSavedMovies = location.pathname === "/saved-movies";

    const opened = props.openBurger ? 'burger__opened' : 'burger__closed';

    return (
        <burger className={opened}>
            <div className='burger__menu'>
                <button className='burger__button-closed' type='button'></button>
                {isLocationMain && <h1 className='burger__main' onClick={goToMain}>Главная</h1>}
                {isLocationMovies && <div className='header_account_logomov' onClick={goToAccount}></div>}
                {isLocationSavedMovies && <div className='header_account_logomov' onClick={goToAccount}></div>}
                <h1 className='burger__'>Главная</h1>
            </div>

        </burger>
    )
}
